import React, { Component } from "react";
import Form from "./form";

const Modal = (props) => {
  if (!props.show) {
    console.log("inside Modal null...Show Modal FALSE");
    return null;
  }
  return (
    <div onClick={props.onClose} className="bg">
      {console.log("button clicked!..show modal TRUE")}
      <div
        onClick={(event) => {
          event.stopPropagation();
        }}
      >
        <Form onClose={props.onClose}/>
      </div>
    </div>
  );
};

export default Modal;
