import React, { useState } from "react";
import Form from "./component/form";
import Modal from "./component/modal";
import AddressList from "./component/AddressList";
import "./App.css";

function App() {
  const addList = [
    {
      name: "Sample",
      mobileNumber: "1234567890",
      address: "a a a",
      city: "Chennai",
      state: "Tamil Nadu",
      postalCode: "123456",
      addressStatus: "Present",
      addressType: "Personal",
    },
    {
      name: "cool",
      mobileNumber: "1234567890",
      address: "a a a",
      city: "Chennai",
      state: "Tamil Nadu",
      postalCode: "123456",
      addressStatus: "Both",
      addressType: "Business",
    },
  ];
  const personal = [
    {
      name: "Sample",
      mobile: "1234567890",
      addrss: "a a a",
      city: "Chennai",
      states: "Tamil Nadu",
      zip: "123456",
      type: "Present",
    },
  ];
  const business = [
    {
      name: "cool",
      mobile: "1234567890",
      addrss: "a a a",
      city: "Chennai",
      states: "Tamil Nadu",
      zip: "123456",
      type: "Both",
    },
  ];
  const [addressList, setAddressList] = useState(addList); 
  const [personalAddList, setPersonalAddList] = useState(personal);
  const [businessAddList, setBusinessAddList] = useState(business);
  const [showModal, setShowModal] = useState(false);
  const onClose = () => {
    setShowModal(false);
  };
  const addButtonHandler = () => {
    setShowModal(true);
  };
  const addingNewAddress = (details) => {
    setAddressList((prevAddressList) => {
      return [...prevAddressList, details];
    });
    setShowModal(false);
  };

  return (
    <React.Fragment>
      <h2 className="App-header">Address Book</h2>
      <div>
        <button className="add" onClick={addButtonHandler}>
          Add
        </button>
        <Modal show={showModal} onClose={onClose}>
          <Form onClose={onClose} onAddAddress={addingNewAddress}></Form>
        </Modal>
        <AddressList addressList={addressList} personal={personalAddList} business={businessAddList} />
      </div>
    </React.Fragment>
  );
}

export default App;
