import React, { useState } from "react";

const Form = () => {
  const [name, setName] = useState();
  const [mobileNumber, setMobileNumber] = useState();
  const [address, setAddress] = useState();
  const [city, setCity] = useState();
  const [state, setState] = useState();
  const [postalCode, setPostalCode] = useState();
  const [AddressType, setAddressType] = useState("");
  const [AddressStatus, setAddressStatus] = useState();
  const [checkedStatus, setCheckedStatus] = useState({
    present: false,
    permanent: false,
    both: false,
  });
  const clearForm = () => {
    setName("");
    setAddress("");
    setMobileNumber("");
    setState("");
    setCity("");
    setPostalCode("");
    setAddressStatus("");
    setCheckedStatus({ present: false, permanent: false, both: false });
  };
  const nameHandler = (event) => {
    setName(event.target.value);
  };
  const mobileNumberHandler = (event) => {
    setMobileNumber(event.target.value);
  };
  const addressHandler = (event) => {
    setAddress(event.target.value);
  };
  const stateHandler = (event) => {
    setState(event.target.value);
  };
  const cityHandler = (event) => {
    setCity(event.target.value);
  };
  const postalCodeHandler = (event) => {
    setPostalCode(event.target.value);
  };
  const addressTypeHandler = (event) => {
    setAddressType(event.target.value);
    if (AddressType !== "") {
      clearForm();
    }
  };
  const addressStatusHandler = (event) => {
    setAddressStatus(event.target.value);
    setCheckedStatus((prevCheckedStatus) => {
      return { ...prevCheckedStatus, [event.target.value]: true };
    });
  };
  const onSubmitHandler = (event) => {
    event.preventDefault();
    const details = {
      addressType: AddressType,
      name: name,
      mobileNumber: mobileNumber,
      address: address,
      city: city,
      state: state,
      postalCode: postalCode,
      addressStatus: AddressStatus,
    };
    console.log(details);
    clearForm();
  };

  return (
    <React.Fragment>
      <div onChange={addressTypeHandler}>
        <input
          type="radio"
          id="personal"
          name="address-type"
          value="personal"
        />
        <label htmlFor="personal">Personal</label>
        <input
          type="radio"
          id="business"
          name="address-type"
          value="business"
        />
        <label htmlFor="permanent">Business</label>
      </div>
      {AddressType && (
        <form onSubmit={onSubmitHandler} id="address-form">
          <div>
            <div>
              <label>Name</label>
              <input type="text" onChange={nameHandler} value={name}/>
            </div>
            <div>
              <label>Mobile No.</label>
              <input type="number" onChange={mobileNumberHandler} value={mobileNumber} />
            </div>
            <div>
              <label>Address</label>
              <input type="text" onChange={addressHandler} value={address} />
            </div>
            <div>
              <label>City</label>
              <input type="text" onChange={cityHandler} value={city}/>
            </div>
            <div>
              <label>State</label>
              <input type="text" onChange={stateHandler}  value={state} />
            </div>
            <div>
              <label>Postal Code/Zip Code</label>
              <input type="number" onChange={postalCodeHandler} value={postalCode}/>
            </div>
          </div>
          <div >
            <input
              type="radio"
              id="present"
              name="address-status"
              value="present"
              checked={checkedStatus.present}
              onChange={addressStatusHandler}
            />
            <label htmlFor="present">Present</label>
            <input
              type="radio"
              id="permanent"
              name="address-status"
              value="permanent"
              checked={checkedStatus.permanent}
              onChange={addressStatusHandler}
            />
            <label htmlFor="permanent">Permanent</label>
            <input
              type="radio"
              id="both"
              name="address-status"
              value="both"
              checked={checkedStatus.both}
              onChange={addressStatusHandler}
            />
            <label htmlFor="both">Both</label>
          </div>
          <div>
            <button type="reset">Clear</button>
            <button type="submit">Save</button>
          </div>
        </form>
      )}
    </React.Fragment>
  );
};

export default Form;
