import React, { useState, useRef } from "react";

const Form = () => {
  const name = useRef();
  const mobileNumber = useRef();
  const address = useRef();
  const city = useRef();
  const state = useRef();
  const postalCode = useRef();
  const [AddressType, setAddressType] = useState('');
  const [AddressStatus, setAddressStatus] = useState('');
  const addressTypeHandler = (event) => {
    setAddressType(event.target.value);
    document.getElementById("address-form").reset();
  };
  const addressStatusHandler = (event) => {
    setAddressStatus(event.target.value);
  };
  const onSubmitHandler = (event) => {
      event.preventDefault();
    const details = {
      name: name.current.value,
      mobileNumber: mobileNumber.current.value,
      address: address.current.value,
      city: city.current.value,
      state: state.current.value,
      postalCode: postalCode.current.value,
      addressStatus: AddressStatus
    };
    console.log(details);
    
  };
  console.log(AddressType);
  console.log(AddressStatus);

  return (
    <React.Fragment>
      <div onChange={addressTypeHandler}>
        <input
          type="radio"
          id="personal"
          name="address-type"
          value="personal"
        />
        <label htmlFor="personal">Personal</label>
        <input
          type="radio"
          id="business"
          name="address-type"
          value="business"
        />
        <label htmlFor="permanent">Business</label>
      </div>
      {AddressType && <form onSubmit={onSubmitHandler} id="address-form">
        <div>
          <div>
            <label>Name</label>
            <input type="text" ref={name} />
          </div>
          <div>
            <label>Mobile No.</label>
            <input type="number" ref={mobileNumber} />
          </div>
          <div>
            <label>Address</label>
            <input type="text" ref={address} />
          </div>
          <div>
            <label>City</label>
            <input type="text" ref={city} />
          </div>
          <div>
            <label>State</label>
            <input type="text" ref={state} />
          </div>
          <div>
            <label>Postal Code/Zip Code</label>
            <input type="number" ref={postalCode} />
          </div>
        </div>
        <div onChange={addressStatusHandler}>
          <input
            type="radio"
            id="present"
            name="address-status"
            value="present"
          />
          <label htmlFor="present">Present</label>
          <input
            type="radio"
            id="permanent"
            name="address-status"
            value="permanent"
          />
          <label htmlFor="permanent">Permanent</label>
          <input type="radio" id="both" name="address-status" value="both" />
          <label htmlFor="both">Both</label>
        </div>
        <div>
          <button type="reset">Clear</button>
          <button type="submit">Save</button>
        </div>
      </form>}
    </React.Fragment>
  );
};

export default Form;
