import React, { useState } from "react";

const Form = () => {
  const [name, setName] = useState("");
  const [mobileNumber, setMobileNumber] = useState("");
  const [address, setAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [AddressType, setAddressType] = useState("");
  const [AddressStatus, setAddressStatus] = useState("");
  const [checkedStatus, setCheckedStatus] = useState({
    present: false,
    permanent: false,
    both: false,
  });
  const clearForm = () => {
    setName("");
    setAddress("");
    setMobileNumber("");
    setState("");
    setCity("");
    setPostalCode("");
    setAddressStatus("");
    setCheckedStatus({ present: false, permanent: false, both: false });
  };
  const nameHandler = (event) => {
    setName(event.target.value);
  };
  const mobileNumberHandler = (event) => {
    setMobileNumber(event.target.value);
  };
  const addressHandler = (event) => {
    setAddress(event.target.value);
  };
  const stateHandler = (event) => {
    setState(event.target.value);
  };
  const cityHandler = (event) => {
    setCity(event.target.value);
  };
  const postalCodeHandler = (event) => {
    setPostalCode(event.target.value);
  };
  const addressTypeHandler = (event) => {
    setAddressType(event.target.value);
    if (AddressType !== "") {
      clearForm();
    }
  };
  const addressStatusHandler = (event) => {
    setAddressStatus(event.target.value);
    setCheckedStatus((prevCheckedStatus) => {
      return { ...prevCheckedStatus, [event.target.value]: true };
    });
  };
  const onSubmitHandler = (event) => {
    event.preventDefault();
    const details = {
      addressType: AddressType,
      name: name,
      mobileNumber: mobileNumber,
      address: address,
      city: city,
      state: state,
      postalCode: postalCode,
      addressStatus: AddressStatus,
    };
    console.log(details);
    clearForm();
  };

  return (
    <React.Fragment>
      
      <div onChange={addressTypeHandler} className='radios'>
        <div className="radio">
        <input
          type="radio"
          id="personal"
          name="address-type"
          value="personal"
        />
        <label htmlFor="personal">Personal</label>
        </div>
        <div className="radio">
        <input
          type="radio"
          id="business"
          name="address-type"
          value="business"
        />
        <label htmlFor="permanent">Business</label>
        </div>
      </div>
      {AddressType && (
        <form onSubmit={onSubmitHandler} id="address-form" className="pop">
          <div>
            <div>
              <label>Name</label>
              <input type="text" onChange={nameHandler} value={name} className='fields'/>
            </div>
            <div>
              <label>Mobile No.</label>
              <input
                type="number"
                onChange={mobileNumberHandler}
                value={mobileNumber}
                className='fields'
              />
            </div>
            <div>
              <label>Address</label>
              <textarea type="text" onChange={addressHandler} value={address} className='fields' />
            </div>
            <div>
              <label>City</label>
              <input type="text" onChange={cityHandler} value={city} className='fields' />
            </div>
            <div>
              <label>State</label>
              <input type="text" onChange={stateHandler} value={state} className='fields' />
            </div>
            <div>
              <label>Postal Code/Zip Code</label>
              <input
                type="number"
                onChange={postalCodeHandler}
                value={postalCode}
                className='fields'
              />
            </div>
          </div>
          <div>
            <input
              type="radio"
              id="present"
              name="address-status"
              value="present"
              checked={checkedStatus.present}
              onChange={addressStatusHandler}
              className='radio fields'
            />
            <label htmlFor="present">Present</label>
            <input
              type="radio"
              id="permanent"
              name="address-status"
              value="permanent"
              checked={checkedStatus.permanent}
              onChange={addressStatusHandler}
              className='fields radio'
            />
            <label htmlFor="permanent">Permanent</label>
            <input
              type="radio"
              id="both"
              name="address-status"
              value="both"
              checked={checkedStatus.both}
              onChange={addressStatusHandler}
            />
            <label htmlFor="both">Both</label>
          </div>
          <div>
            <button type="submit" className="save btns">Save</button>
            <button type="reset" className="clear btns">Clear</button>
          </div>
        </form>
      )}
    </React.Fragment>
  );
};

export default Form;
