import Form from "./form";
import React, { useState } from "react";

function AddressList() {
  const addList = [
    {
      name: "Ankit",
      mobileNumber: "123456",
      address: "sdfgsds",
      state: "fgdfhdf",
      city: "dfghd",
      postalCode: "134",
      addressStatus: "present",
      addressType: "Personal",
    },
    {
      name: "Fasttrack",
      mobileNumber: "123456",
      address: "sdfgsds",
      state: "fgdfhdf",
      city: "dfghd",
      postalCode: "134",
      addressStatus: "present",
      addressType: "Business",
    },
  ];
  const [AddressType, setAddressType] = useState("Personal");
  const [addressList, setAddressList] = useState(addList);

  const onTabChangeToPersonal = () => {
    setAddressType('Personal');
  };
  const onTabChangeToBusiness = () => {
    setAddressType('Business');
  };
  console.log(AddressType);

  return (
    <React.Fragment>
      <div>
        <button className="add">Add</button>
      </div>
      <div>
        <table>
          <thead>
            <tr>
              <th className="buttons" value="Personal" onClick={onTabChangeToPersonal}>
                Personal
              </th>
              <th className="buttons" value="Business" onClick={onTabChangeToBusiness}>
                Business
              </th>
            </tr>
            <tr>
              <th>Name</th>
              <th>Mobile No.</th>
              <th>Address</th>
              <th>City</th>
              <th>State</th>
              <th>Zip</th>
              <th>Present/Permanent</th>
            </tr>
          </thead>
          <tbody>
            {addressList.map(
              (addr) =>
                addr.addressType === AddressType && (
                  <tr key={addr.mobileNumber}>
                    <td>{addr.name}</td>
                    <td>{addr.mobileNumber}</td>
                    <td>{addr.address}</td>
                    <td>{addr.city}</td>
                    <td>{addr.state}</td>
                    <td>{addr.postalCode}</td>
                    <td>{addr.addressStatus}</td>
                  </tr>
                )
            )}

            {/* <tr>
              <td>{addressList[0].name}</td>
              <td>{addressList[0].mobileNumber}</td>
              <td>{addressList[0].address}</td>
              <td>{addressList[0].city}</td>
              <td>{addressList[0].state}</td>
              <td>{addressList[0].postalCode}</td>
              <td>{addressList[0].addressStatus}</td>
            </tr> */}
            {/* {addList.forEach((addr) => (
              <tr>
                <td>{addr.name}</td>
                <td>{addr.mobileNumber}</td>
                <td>{addr.address}</td>
                <td>{addr.city}</td>
                <td>{addr.state}</td>
                <td>{addr.postalCode}</td>
                <td>{addr.addressStatus}</td>
              </tr>
            ))} */}
          </tbody>
        </table>
      </div>
    </React.Fragment>
  );
}

export default AddressList;
