import React from "react";

const Modal = (props) => {
  if (!props.show) {
    return null;
  }
  return (
    <div onClick={props.onClose} className="bg">
      <div
        onClick={(event) => {
          event.stopPropagation();
        }}
      >
        {props.children}
      </div>
    </div>
  );
};

export default Modal;
