import React, { useState } from "react";
import Form from "./component/form";
import Modal from "./component/modal";
import AddressList from "./component/AddressList";
import "./App.css";

function App() {
  const addList = [
    {
      name: "Ankit",
      mobileNumber: "123456",
      address: "sdfgsds",
      state: "fgdfhdf",
      city: "dfghd",
      postalCode: "134",
      addressStatus: "Present",
      addressType: "Personal",
    },
    {
      name: "Fastrack",
      mobileNumber: "123456",
      address: "sdfgsds",
      state: "fgdfhdf",
      city: "dfghd",
      postalCode: "134",
      addressStatus: "Present",
      addressType: "Business",
    },
  ];
  const [addressList, setAddressList] = useState(addList);
  const [showModal, setShowModal] = useState(false);
  const onClose = () => {
    setShowModal(false);
  };
  const addButtonHandler = () => {
    setShowModal(true);
  };
  const addingNewAddress = (details) => {
    setAddressList((prevAddressList) => {
      return [...prevAddressList, details];
    });
    setShowModal(false);
  };

  return (
    <React.Fragment>
      <h2 className="App-header">Address Book</h2>
      <div>
        <button className="add" onClick={addButtonHandler}>
          Add
        </button>
        <Modal show={showModal} onClose={onClose}>
          <Form onClose={onClose} onAddAddress={addingNewAddress}></Form>
        </Modal>
        <AddressList addressList={addressList} />
      </div>
    </React.Fragment>
  );
}

export default App;
