import React, { useState, useRef } from "react";

const Form = () => {
  const [name, setName] = useState();
  const [mobileNumber, setMobileNumber] = useState();
  const [address, setAddress] = useState();
  const [city, setCity] = useState();
  const [state, setState] = useState();
  const [postalCode, setPostalCode] = useState();
  const [AddressType, setAddressType] = useState("");
  const [AddressStatus, setAddressStatus] = useState();
  const [checkedStatus, setCheckedStatus] = useState({
    present: false,
    permanent: false,
    both: false,
  });
  const clearForm = () => {
    name.current.value = "";
    mobileNumber.current.value = "";
    address.current.value = "";
    city.current.value = "";
    state.current.value = "";
    postalCode.current.value = "";
    setAddressStatus("");
  };
  const nameHandler = (event) => {
    setName(event.target.value);
  };
  const mobileNumberHandler = (event) => {
    setMobileNumber(event.target.value);
  };
  const addressHandler = (event) => {
    setAddress(event.target.value);
  };
  const stateHandler = (event) => {
    setState(event.target.value);
  };
  const cityHandler = (event) => {
  setCity(event.target.value);
  };
  const postalCodeHandler = (event) => {
    setPostalCode(event.target.value);
  };
  const addressTypeHandler = (event) => {
    setAddressType(event.target.value);
    if (AddressType !== "") {
      clearForm();
    }
  };
  const addressStatusHandler = (event) => {
    setAddressStatus(event.target);
    console.log(AddressStatus.value);
    console.log(AddressStatus.checked);
  };
  const onSubmitHandler = (event) => {
    event.preventDefault();
    const details = {
      addressType: AddressType,
      name: name.current.value,
      mobileNumber: mobileNumber.current.value,
      address: address.current.value,
      city: city.current.value,
      state: state.current.value,
      postalCode: postalCode.current.value,
      addressStatus: AddressStatus,
    };
    console.log(AddressStatus.value);
    console.log(AddressStatus.checked);
    console.log(details);
    clearForm();
  };

  return (
    <React.Fragment>
      <div onChange={addressTypeHandler}>
        <input
          type="radio"
          id="personal"
          name="address-type"
          value="personal"
        />
        <label htmlFor="personal">Personal</label>
        <input
          type="radio"
          id="business"
          name="address-type"
          value="business"
        />
        <label htmlFor="permanent">Business</label>
      </div>
      {AddressType && (
        <form onSubmit={onSubmitHandler} id="address-form">
          <div>
            <div>
              <label>Name</label>
              <input type="text" onChange={nameHandler} />
            </div>
            <div>
              <label>Mobile No.</label>
              <input type="number" onChange={mobileNumberHandler} />
            </div>
            <div>
              <label>Address</label>
              <input type="text" onChange={addressHandler} />
            </div>
            <div>
              <label>City</label>
              <input type="text" onChange={cityHandler} />
            </div>
            <div>
              <label>State</label>
              <input type="text" onChange={stateHandler} />
            </div>
            <div>
              <label>Postal Code/Zip Code</label>
              <input type="number" onChange={postalCodeHandler} />
            </div>
          </div>
          <div onChange={addressStatusHandler}>
            <input
              type="radio"
              id="present"
              name="address-status"
              value="present"
              // checked={AddressStatus.present}
            />
            <label htmlFor="present">Present</label>
            <input
              type="radio"
              id="permanent"
              name="address-status"
              value="permanent"
              // checked={AddressStatus.permanent}
            />
            <label htmlFor="permanent">Permanent</label>
            <input
              type="radio"
              id="both"
              name="address-status"
              value="both"
              // checked={AddressStatus.both}
            />
            <label htmlFor="both">Both</label>
          </div>
          <div>
            <button type="reset">Clear</button>
            <button type="submit">Save</button>
          </div>
        </form>
      )}
    </React.Fragment>
  );
};

export default Form;
